import React, { useEffect, useState } from "react";
import { Image, View, Text, StyleSheet, Switch, TouchableOpacity } from 'react-native';

export default function TodoItem(props) {
    console.log(props.item);
    const [done, setDone] = useState(props.item.done);

    useEffect(() => {
        setDone(done);
    }, [props.item.done]);

    const valueChange = (state) => {
        props.setCountDone(state);
        setDone(!state);
        props.item.done = !state;
    }

    return (
        <View style={styles.content}>
            <Switch value={done} onValueChange={() => valueChange(done)} />
            <Text style={[styles.text_item, { textDecorationLine: done ? 'line-through' : 'none' }]}>{props.item.content}</Text>
            <TouchableOpacity onPress={() => props.delete(props.item.id)}>
                <Image source={require('../assets/trash-can-outline.png')} style={{ height: 24, width: 24 }} />
            </TouchableOpacity>
        </View>
    )
}

const styles = StyleSheet.create({
    content: {
        flexDirection: 'row'
    },
    text_item: {
        marginLeft: 10,
        width: 150
    }
})
