import { setStatusBarNetworkActivityIndicatorVisible } from "expo-status-bar";
import React, { useState } from "react";
import {
  StyleSheet,
  View,
  TextInput,
  Button,
  Text,
  FlatList,
  Switch,
} from "react-native";

import todoData from "../Helpers/todoData";
import TodoItem from "./TodoItem";

export default function TodoList() {
  const [count, setCount] = useState(
    todoData.filter((item) => item.done).length
  );
  const [todos, setTodos] = useState(todoData);
  const [todosSave, setTodosSave] = useState(todoData);
  const [newTodoText, setNewTodoText] = useState("");

  const countDone = (done) => {
    if (done == false) {
      setCount(count + 1);
    } else {
      setCount(count - 1);
    }
  };

  const deleteTodo = (id) => {
    const newTodos = todoData.filter((item) => item.id != id);
    setTodos(newTodos);
    setTodosSave(newTodos);
    setCount(newTodos.filter((item) => item.done).length);
  };

  const addNewTodo = () => {
    setNewTodoText("");
    const monid = Math.max(...todos.map((item) => item.id)) + 1;
    setTodos([...todos, { id: monid, content: newTodoText, done: false }]);
    setTodosSave([...todos, { id: monid, content: newTodoText, done: false }]);
  };

  const showInprocess = () => {
    setTodos(todosSave.filter((item) => item.done == false));
  };

  const showDone = () => {
    setTodos(todosSave.filter((item) => item.done == true));
  };

  const checkAll = () => {
    const newTodos = todosSave.map((item) => {
      item.done = true;
      return item;
    });
    setTodos(newTodos);
    setTodosSave(newTodos);
    setCount(newTodos.filter((item) => item.done).length);
  };

  const uncheckAll = () => {
    const newTodos = todosSave.map((item) => {
      item.done = false;
      return item;
    });
    setTodos(newTodos);
    setTodosSave(newTodos);
    setCount(newTodos.filter((item) => item.done).length);
  };

  return (
    <View style={styles.view}>
      <Text style={styles.titre}>My TodoList</Text>
      <View style={styles.filter}>
        <text>Filtrer par : </text>
        <Button
          className={styles.button}
          title="todos en cours"
          onPress={() => showInprocess()}
        />
        <Button
          style={styles.button}
          title="todos faites"
          onPress={() => showDone()}
        />
        <Button title="tous les todos" onPress={() => setTodos(todosSave)} />
      </View>

      <View style={styles.filter}>
        <Text>Cocher/décocher</Text>
        <Button title="cocher toutes les todos" onPress={() => checkAll()} />
        <Button
          title="décocher toutes les todos"
          onPress={() => uncheckAll()}
        />
      </View>

      <FlatList
        style={styles.liste}
        data={todos}
        renderItem={({ item }) => (
          <TodoItem item={item} setCountDone={countDone} delete={deleteTodo} />
        )}
      />

      <TextInput
        onChangeText={setNewTodoText}
        placeholder="saisir ici un nouvel item"
        onSubmitEditing={addNewTodo}
        value={newTodoText}
      />
      <Text>{count} items ont été réalisés</Text>
    </View>
  );
}

const styles = StyleSheet.create({
    view: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
  titre: {
    fontSize: 30,
    textAlign: "center",
    marginTop: 20,
    marginBottom: 20,
    fontWeight: "bold",
  },
  filter: {
    marginBottom: 10,
    gap: 10,
    alignItems: "center",
  },liste: {
    paddingTop: 10,
    width: "100%",
    flex: 1,
    //alignItems: 'center',
    justifyContent: 'center',
    },
});
