import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  View,
  TextInput,
  Button,
  Text,
  FlatList,
  Switch,
} from "react-native";

import TodoItem from "./TodoItem";

export default function TodoList(props) {
  const [todoData, setData] = useState(props.data);

  useEffect(() => {
    setData(props.data);
    setTodos(props.data);
    setTodosSave(props.data);
    setCount(props.data.filter((item) => item.done).length);
}, [props.data]);


  const [count, setCount] = useState(
    todoData.filter((item) => item.done).length
  );

  const [todos, setTodos] = useState(todoData);
  const [todosSave, setTodosSave] = useState(todoData);

  const countDone = (done) => {
    if (done == false) {
      setCount(count + 1);
    } else {
      setCount(count - 1);
    }
  };

  const deleteTodo = (id) => {
    const newTodos = todoData.filter(item => item.id != id);
    setTodos(newTodos);
    setTodosSave(newTodos);
    setCount(newTodos.filter((item) => item.done).length);
  };

  function showInprocess() {
    setTodos(todosSave.filter((item) => item.done == false));
  }

  const showDone = () => {
    setTodos(todosSave.filter((item) => item.done == true));
  };

  const checkAll = () => {
    const newTodos = todosSave.map((item) => {
      item.done = true;
      return item;
    });
    setTodos(newTodos);
    setTodosSave(newTodos);
    setCount(newTodos.filter((item) => item.done).length);
  };

  const uncheckAll = () => {
    const newTodos = todosSave.map((item) => {
      item.done = false;
      return item;
    });
    setTodos(newTodos);
    setTodosSave(newTodos);
    setCount(newTodos.filter((item) => item.done).length);
  };

  return (
    console.log("todo", todoData),
    <View style={styles.view}>
      <Text style={styles.titre}>My TodoList</Text>
      <Text>Filtrer par : </Text>
      <View style={styles.filter}>
        <Button
          className={styles.button}
          title="todos en cours"
          onPress={() => showInprocess()}
        />
        <Button
          style={styles.button}
          title="todos faites"
          onPress={() => showDone()}
        />
        <Button title="tous les todos" onPress={() => setTodos(todosSave)} />
      </View>

      <Text>Cocher/décocher</Text>
      <View style={styles.filter}>
        <Button title="cocher toutes les todos" onPress={() => checkAll()} />
        <Button
          title="décocher toutes les todos"
          onPress={() => uncheckAll()}
        />
      </View>

      <View style={styles.items}>
      <FlatList
        style={styles.liste}
        data={todos}
        renderItem={({ item }) => (
          <TodoItem item={item} setCountDone={countDone} delete={deleteTodo}/>
        )}
      />
      </View>


      <TextInput
        onChangeText={setNewTodoText}
        placeholder="saisir ici un nouvel item"
        onSubmitEditing={addNewTodo}
        value={newTodoText}
      />
      <Text>{count} items ont été réalisés</Text>
    </View>
  );
}

const styles = StyleSheet.create({
    view: {
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        flexDirection: 'column',
        flex: 1,
    },
  titre: {
    fontSize: 30,
    textAlign: "center",
    marginTop: 20,
    marginBottom: 20,
    fontWeight: "bold",
  },
  filter: {
    marginBottom: 10,
    gap: 10,
    flexFlow: "row wrap",
    justifyContent: 'center',
  },
  liste: {
    paddingTop: 10,
    width: "100%",
    flex: 1,
    },
    items: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        gap: 10,
    },
    Button: {
      fontSize: 10,
    }

});
