import { Animated, StyleSheet, Text, View } from 'react-native';
import React, { useEffect, useState, useRef } from 'react';
import Constants from 'expo-constants';
export default function ProgressBar(props) {
  const counter = useRef(new Animated.Value(0)).current;
  const countInterval = useRef(null);
  const [count, setCount] = useState(50);
  // EFFECT HOOK TO SETUP AND CLEAN INTERvAL COUNTER
  useEffect(() => {
    console.log(props.total)
    // SETUP INTERVAL COUNTER TO REFERENCED HOOK
    countInterval.current = setInterval(() => setCount((prev) => prev), 1000);
    return () => {
      // CLEAR ON EXIT
      //clearInterval(countInterval);
    };
  }, []);
  // EFFECT HOOK TO TRACK CHANGES IN PROGRESS
  useEffect(() => {
    // TRIGGER VIEW UPDATE
    load(count)
  }, [count]);

  // FUNCTION TO ANIMATE VIEW
  const load = (value) => {
    // UPDATE ANIMATABLE VIEW
    Animated.timing(counter, {
      toValue: value,
      duration: 0,
      useNativeDriver: false,
    }).start();
  };
  const width = counter.interpolate({
    inputRange: [0, 100],
    outputRange: ["0%", "100%"],
    extrapolate: "clamp"
  })

  return (
    setCount(props.total),
      <View style={styles.progressBar}>
        <Animated.View style={{ ...StyleSheet.absoluteFill, backgroundColor: "#46FF33", width: width }}/>
      </View>

  );
}
const styles = StyleSheet.create({
  progressBar: {
   width: '80%',
   height: 40,
   backgroundColor: '#fff',
   borderWidth: 3,
   borderRadius: 8,
   borderColor: '#555',
   flexDirection:"row"
 }
});
