import React, { useEffect, useState } from "react";

import TodoList from "../components/TodoList";
import { getTodoLists } from '../API/todoAPI'
//import todoData from "../Helpers/todoData";
import { TokenContext } from "../Context/Context";

export default function TodoLists() {
  const [data, setData] = useState();

  const getTodo = (token) => {
            getTodoLists(token)
            .then(data => {
              setData(data);
            }
            )
            .catch(err => {
              console.log(err);
            })
      }     

  useEffect(() => {
   if(data.length == 0) {
    console.log('chargement initial');
    <TokenContext.Consumer>
      {([token, setToken])=> {
        return (
          getTodo(token)
        )
      }
      }
    </TokenContext.Consumer>
          setData(getTodo())
    }else{
      console.log('chargement suivant');
      const data2 = getTodo();
      console.log(data2);
    }
  }, [data]);

  return <TodoList/>;
}

