import { setStatusBarNetworkActivityIndicatorVisible } from "expo-status-bar";
import React, { useState } from "react";
import { StyleSheet, View, TextInput, Button, Text, FlatList, Switch } from 'react-native';

import todoData from '../Helpers/todoData';
import TodoItem from './TodoItem';

export default function TodoList(){
    const [count, setCount] = useState(todoData.filter((item) => item.done).length);
    const [todos, setTodos] = useState(todoData);
    const [todosSave, setTodosSave] = useState(todoData);
    const [newTodoText, setNewTodoText] = useState('');

    const countDone = (done) => {
        if (done == false){
            setCount(count+1) 
        }else{
            setCount(count-1)
        }
    }

    const deleteTodo = (id) => {
        const newTodos = todoData.filter((item) => item.id != id);
        setTodos(newTodos)
        setTodosSave(newTodos)
        setCount(newTodos.filter(item => item.done).length)
    }

    const addNewTodo = () => {
        setNewTodoText('');
        const monid = Math.max(...todos.map(item => item.id)) + 1;
        setTodos([...todos, {id: monid, content: newTodoText, done: false}])
        setTodosSave([...todos, {id: monid, content: newTodoText, done: false}])
    }

    const showInprocess = () => {
            setTodos(todosSave.filter((item) => item.done == false));
    }

    const showDone = () => {
            setTodos(todosSave.filter((item) => item.done == true));
    }

    return (
        <View>
             <FlatList
            style={{ paddingLeft: 10 }}
            data={todos}
            renderItem={({item}) => <TodoItem item={item} setCountDone={countDone} delete={deleteTodo} />}
             />
             <TextInput
                onChangeText={setNewTodoText}
                placeholder='saisir ici un nouvel item'
                onSubmitEditing={addNewTodo}
                value={newTodoText}
            />    
             <Text>{count} items ont été réalisés</Text>
             <Button title='todos en cours' onPress={() => showInprocess()} />
             <Button title='todos faites' onPress={() => showDone()} /> 
             <Button title='tous les todos' onPress={() => setTodos(todosSave)} />     
        </View>

    )
}
