import { Image, TouchableOpacity, View } from 'react-native';
import { deleteTodoList } from "../../API/todoAPI";

export default function Delete(props) {

    const [token, setToken] = useState(props.token);
    const [error, setError] = useState("");
    const [choix, setChoix] = useState(props.choix);

    const getDeleteTask = (token, id) => {
        setError("");
        deleteTodoList(id,token)
          .then(() => {
          })
          .catch((err) => {
            setError(err.message);
          });
      };

    const deleteFunction = (token, id) => {
        if(choix == "todo"){
            props.deleteTodo(props.id);
            getDeleteTask(token, props.id);
        }else{
            console.log("users")
        }
    }

    return (
        <View>
            <TouchableOpacity onPress={() => deleteFunction(token)}>
                <Image source={require('../../assets/trash-can-outline.png')} style={{ height: 24, width: 24 }} />
            </TouchableOpacity>
        </View>
    )

}
