import { setStatusBarNetworkActivityIndicatorVisible } from "expo-status-bar";
import React, { useState } from "react";
import { StyleSheet, View, TextInput, Button, Text, FlatList, Switch } from 'react-native';

import todoData from '../Helpers/todoData';
import TodoItem from './TodoItem';

export default function TodoList(){
    const [count, setCount] = useState(todoData.filter((item) => item.done).length);

    const countDone = (done) => {
        if (done == false){
            setCount(count+1) 
        }else{
            setCount(count-1)
        }
    }

    const deleteTodo = (id) => {
        const newTodo = todoData.filter((item) => item.id != id);
        setTodos(newTodo)
        setCount(newTodos.filter(item => item.done).length)
    }

    return (
        <View>
             <FlatList
            style={{ paddingLeft: 10 }}
            data={todoData}
            renderItem={({item}) => <TodoItem item={item} setCountDone={countDone} delete={deleteTodo} />}
             />
             <Text>{count} items ont été réalisés</Text>      
        </View>

    )
}
