import React, { useEffect, useState } from "react";

import TodoList from "../components/TodoList";
import { getTodoLists } from '../API/todoAPI'
import todoData from "../Helpers/todoData";
import { TokenContext } from "../Context/Context";



export default function TodoLists() {
  const [data, setData] = useState(todoData);

  const getTodo = () => {
            getTodoLists()
            .then(data => {
              setData(data);
            }
            )
            .catch(err => {
              console.log(err);
            }
            );
          }        

  useEffect(() => {
   if(data.length == 0) {
    console.log('chargement initial');
          setData(getTodo())
    }else{
      console.log('chargement suivant');
      <TokenContext.Consumer>
        {([token, setToken]) => {
          console.log('a');
          getTodoLists(token)
          .then(data => {
            setData(data);
          }
          )
          .catch(err => {
            console.log(err);
          }
          );}
        }
      </TokenContext.Consumer>
    }
  }, [data]);

  return <TodoList/>;
}

